import React, { useState } from 'react';
import './Services.css';

const Services = () => {
  const [hoveredCard, setHoveredCard] = useState(null);

  const services = [
    {
      icon: 'üíª',
      title: 'Web Development',
      description: 'Custom websites and web applications built with modern technologies and best practices.',
      features: ['Responsive Design', 'Performance Optimization', 'SEO Ready', 'Mobile First'],
      color: '#667eea',
      gradient: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    },
    {
      icon: 'üì±',
      title: 'Mobile Apps',
      description: 'Native and cross-platform mobile applications that deliver exceptional user experiences.',
      features: ['iOS & Android', 'Cross-Platform', 'UI/UX Design', 'App Store Optimization'],
      color: '#f093fb',
      gradient: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)'
    },
    {
      icon: 'üé®',
      title: 'UI/UX Design',
      description: 'Beautiful, intuitive designs that engage users and drive conversions.',
      features: ['User Research', 'Wireframing', 'Prototyping', 'Design Systems'],
      color: '#4facfe',
      gradient: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'
    },
    {
      icon: '‚òÅÔ∏è',
      title: 'Cloud Solutions',
      description: 'Scalable cloud infrastructure and deployment solutions for modern businesses.',
      features: ['AWS/Azure', 'DevOps', 'Microservices', 'Auto Scaling'],
      color: '#43e97b',
      gradient: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)'
    },
    {
      icon: 'üîß',
      title: 'Consulting',
      description: 'Strategic technology consulting to help your business grow and innovate.',
      features: ['Tech Strategy', 'Digital Transformation', 'Process Optimization', 'Training'],
      color: '#fa709a',
      gradient: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)'
    },
    {
      icon: 'üõ°Ô∏è',
      title: 'Security',
      description: 'Comprehensive security solutions to protect your digital assets and data.',
      features: ['Security Audit', 'Penetration Testing', 'Compliance', 'Monitoring'],
      color: '#a8edea',
      gradient: 'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)'
    }
  ];

  return (
    <section id="services" className="services">
      <div className="container">
        <div className="services-header">
          <h2 className="section-title">Our Services</h2>
          <p className="section-description">
            We offer comprehensive digital solutions tailored to meet your business needs 
            and drive growth in the digital landscape.
          </p>
        </div>
        
        <div className="services-grid">
          {services.map((service, index) => (
            <div 
              key={index} 
              className={`service-card ${hoveredCard === index ? 'hovered' : ''}`}
              onMouseEnter={() => setHoveredCard(index)}
              onMouseLeave={() => setHoveredCard(null)}
              style={{ '--card-color': service.color, '--card-gradient': service.gradient }}
            >
              <div className="service-icon">{service.icon}</div>
              <h3 className="service-title">{service.title}</h3>
              <p className="service-description">{service.description}</p>
              <ul className="service-features">
                {service.features.map((feature, featureIndex) => (
                  <li key={featureIndex}>{feature}</li>
                ))}
              </ul>
              <div className="service-glow"></div>
              <div className="service-border"></div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
